import React from 'react';
import { createUseStyles } from 'react-jss';
import { useGetPokemons } from '../../hooks/useGetPokemons';

export const PokemonList = () => {
  const classes = useStyles();
  const { pokemons, loading } = useGetPokemons();


  return (
    <div className={classes.root}>
      {loading && <div>Loading...</div>}
      {pokemons.map((pkmn) => (
        <div key={pkmn.id} className={classes.container}>
          <h4 className={classes.title}>{pkmn.name}</h4>
          <h5>#{pkmn.number}</h5>
          {pkmn.types.map((type) => <span className={`types ${type.toLowerCase()}`}>{type}&nbsp;</span>)}
          <img src={pkmn.image} alt={pkmn.name} />
        </div>
      ))}
    </div>
  );
};

const useStyles = createUseStyles(
  {
    root: {
      width: '100%',
      textAlign: 'center',
      padding: '32px',
      boxSizing: 'border-box',
      display: 'flex',
      flexWrap: 'wrap',
      gap: '20px',
    },
    container: {
      width: '300px',
      border: '1px solid red',
      '&:hover': {
        backgroundColor: 'red'
      },
    },
    title: {
      margin: 0
    },
    types: {
      color: 'yellow',
      '& .fire': {
        color: 'red',
      },
    }
  },
  { name: 'PokemonList' }
);
