import React, { useState } from 'react';
import { createUseStyles } from 'react-jss';
import { Pokemon, useGetPokemons } from '../../hooks/useGetPokemons';
import { Modal } from '../Modal';

export const PokemonList = () => {
  const classes = useStyles();
  const { pokemons, loading } = useGetPokemons();

  const [modalOpen, updateModalOpen] = useState(false);
  const [selectedPokemon, updateSelectedPokemon] = useState('');
  const [searchText, updateSearchText] = useState('');
  const [filteredPokemon, updateFilteredPokemon] = useState([])

  const handleTextChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const query = event.target.value.toLowerCase();
    const results = getResults(query);

    updateFilteredPokemon
    updateSearchText(query);
  };

  const getResults = (query: string): Pokemon[] => {
    return pokemons.reduce((resultArray, pokemon) => {
      // Copy the category, in order to avoid mutating the argument directoy
      const pokemonCopy = { ...pokemon };
      // If there is a match, push it to the resultArray.
      if (pokemon.name.toLowerCase().includes(query)) {
          resultArray.push(pokemonCopy);
      // Else, if the category has children, search for match inside its children
      } else if (pokemon?.types) {
          const subResults = getResults(pokemon.types, query);
          // If there are any matches in the children, push the whole category(from top level) to the results array,
          // so that the array structure can be preserved
          if (subResults.length) {
              pokemonCopy.types = subResults;
              resultArray.push(pokemonCopy);
          }
      }
      return resultArray;
  }, []);
  }

  const handleCardClick = (id: string) => {
    updateSelectedPokemon(id);
    updateModalOpen(true);
  };

  const handleModalClose = () => updateModalOpen(false);

  return (
    <div className={classes.root}>
      <div className={classes.search}>
        <input
          type="text"
          name="search"
          value={searchText}
          onChange={handleTextChange}
          placeholder="Filter PokÃ©mon..."
        />
      </div>
      {loading && <div>Loading...</div>}
      {filteredPokemon.map((pkmn) => (
        <div
          key={pkmn.id}
          className={classes.pokemonCard}
          onClick={() => handleCardClick(pkmn.id)}
        >
          <img src={pkmn.image} alt={pkmn.name} />
          <div className={classes.specs}>
            <h4>{pkmn.name}</h4>
            <h5>#{pkmn.number}</h5>
            {pkmn.types.map((type) => (
              <div
                key={type}
                className={`${classes.types} ${type.toLowerCase()}`}
              >
                {type}&nbsp;
              </div>
            ))}
          </div>
        </div>
      ))}

      <Modal
        pokemonId={selectedPokemon}
        open={modalOpen}
        onClose={handleModalClose}
      />
    </div>
  );
};

const useStyles = createUseStyles(
  {
    root: {
      width: '100%',
      textAlign: 'center',
      padding: '32px',
      boxSizing: 'border-box',

      display: 'flex',
      flexDirection: 'column',
      rowGap: '20px',
    },
    search: {
      display: 'flex',
      width: '100%',
      '& input': {
        color: '#171E2b',
        width: '100%',
        padding: '10px',
      },
    },
    pokemonCard: {
      display: 'flex',
      padding: '10px',
      '& img': {
        maxHeight: '100px',
      },
      '& h4': {
        margin: '5px 0px',
      },
      '& h5': {
        margin: '0px 0px 10px 0px',
      },
      border: '1px solid #7C89A3',
      '&:hover': {
        backgroundColor: 'rgba(0, 0, 0, 0.2)',
        cursor: 'pointer',
      },
    },
    specs: {
      textAlign: 'left',
      marginLeft: '10px',
    },
    types: {
      '&.normal': { color: '#A8A77A' },
      '&.fire': { color: '#EE8130' },
      '&.water': { color: '#6390F0' },
      '&.electric': { color: '#F7D02C' },
      '&.grass': { color: '#7AC74C' },
      '&.ice': { color: '#96D9D6' },
      '&.fighting': { color: '#C22E28' },
      '&.poison': { color: '#A33EA1' },
      '&.ground': { color: '#E2BF65' },
      '&.flying': { color: '#A98FF3' },
      '&.psychic': { color: '#F95587' },
      '&.bug': { color: '#A6B91A' },
      '&.rock': { color: '#B6A136' },
      '&.ghost': { color: '#735797' },
      '&.dragon': { color: '#6F35FC' },
      '&.dark': { color: '#705746' },
      '&.steel': { color: '#B7B7CE' },
      '&.fairy': { color: '#D685AD' },
    },
  },
  { name: 'PokemonList' }
);
