import React, { useState } from 'react';
import { createUseStyles } from 'react-jss';
import { useGetPokemon } from '../../hooks/useGetPokemon';
import clsx from 'clsx';

interface ModalProps {
  pokemonId: string;
  open: boolean;
  onClose: () => void;
}

export const Modal: React.FC<ModalProps> = ({ pokemonId, open, onClose }) => {
  const classes = useStyles();
  const { pokemon, loading } = useGetPokemon(pokemonId);

  return open ? (
    <div className={classes.root}>
      {!loading && (
        <>
          <h1 className={classes.name}>
            {pokemon.name}
            <div
              className={clsx(classes.closeBtn, 'material-icons')}
              onClick={onClose}
            >
              close
            </div>
          </h1>
          <div className={classes.container}>
            <div className={classes.column}>
              <img src={pokemon.image} alt={pokemon.name} />
              <div className={classes.specs}>
                Pok&eacute;mon # {pokemon.number}
                Weight {pokemon.weight.minimum} - {pokemon.weight.maximum}
              </div>
            </div>
            <div className={classes.column}>test</div>
          </div>
        </>
      )}
    </div>
  ) : null;
};

const useStyles = createUseStyles(
  {
    root: {
      backgroundColor: '#0f131b',
      position: 'fixed',
      left: 0,
      right: 0,
      top: 0,
      bottom: 0,
      margin: 'auto',
      maxWidth: '500px',
      height: '400px',
    },
    container: {
      display: 'flex',
      width: '100%',
      padding: '20px',
    },
    column: {
      width: '50%',
      '& img': {
        maxWidth: '200px',
        marginBottom: '10px',
      },
    },
    name: {
      width: '100%',
      fontSize: '1.5em',
      margin: '30px 0',
    },
    closeBtn: {
      position: 'absolute',
      top: '10px',
      right: '10px',
    },
    specs: {
      textAlign: 'left',
    }
  },
  { name: 'PokemonDetails' }
);
