import React from 'react';
import { createUseStyles } from 'react-jss';
import { useGetPokemons } from '../../hooks/useGetPokemons';

export const PokemonList = () => {
  const classes = useStyles();
  const { pokemons, loading } = useGetPokemons();


  return (
    <div className={classes.root}>
      {loading && <div>Loading...</div>}
      {pokemons.map((pkmn) => (
        <div key={pkmn.id} className={classes.container}>
          <h4 className={classes.title}>{pkmn.name}</h4>
          <h5>#{pkmn.number}</h5>
          {pkmn.types.map((type) => 
            <span className={`${classes.types} ${type.toLowerCase()}`}>{type}&nbsp;</span>
          )}
          <img className={classes.image} src={pkmn.image} alt={pkmn.name} />
        </div>
      ))}
    </div>
  );
};

const useStyles = createUseStyles(
  {
    root: {
      width: '100%',
      textAlign: 'center',
      padding: '32px',
      boxSizing: 'border-box',
      display: 'flex',
      flexWrap: 'wrap',
      gap: '20px',
    },
    container: {
      width: '300px',
      border: '1px solid red',
      '&:hover': {
        backgroundColor: 'red'
      },
    },
    title: {
      margin: 0
    },
    types: {
      '&.normal': { color: '#A8A77A' },
      '&.fire': { color: '#EE8130' },
      '&.water': { color: '#6390F0' },
      '&.electric': { color: '#F7D02C' },
      '&.grass': { color: '#7AC74C' },
      '&.ice': { color: '#96D9D6' },
      '&.fighting': { color: '#C22E28' },
      '&.poison': { color: '#A33EA1' },
      '&.ground': { color: '#E2BF65' },
      '&.flying': { color: '#A98FF3' },
      '&.psychic': { color: '#F95587' },
      '&.bug': { color: '#A6B91A' },
      '&.rock': { color: '#B6A136' },
      '&.ghost': { color: '#735797' },
      '&.dragon': { color: '#6F35FC' },
      '&.dark': { color: '#705746' },
      '&.steel': { color: '#B7B7CE' },
      '&.fairy': { color: '#D685AD' },
    },
    image: {
      maxHeight: '150px',
    },
  },
  { name: 'PokemonList' }
);
